import React from 'react';
import PinPopup from './PinPopup.js';

const Pin = (props) => {
  return(
    <PinPopup onHover={
      <div>
          {info.name}
          {info.description}
      </div>}>
      <div className="pin"
         style={{
           backgroundColor: props.info.color,
           top: `${props.info.location[1]/props.browserSize.y*100}%`,
           left: `${props.info.location[0]/props.browserSize.x*100}%`
         }}>
      </div>
   </PinPopup>
  )
}

export default Pin;
