import React, { PureComponent } from 'react';

class PinInput extends PureComponent {

  constructor(props) {
    super(props)
    this.state = {
      name: "",
      description: "",
      color: "#ffffff",
      id: null
    }
  }

  handleColor = (event) => {
    this.setState({color: event.target.value})
  }

  handleName = (event) => {
    this.setState({name: event.target.value})
  }

  handleDescription = (event) => {
    this.setState({description: event.target.value})
  }

  render() {
    return (
      <div className="pin-input">
        <form onSubmit={event => this.props.handleSubmit(event)}>
          <label> Name </label><br />
            <input type="text" onChange={this.handleName} value={this.state.name} name="name" id="name" /><br />
          <label> Description </label><br />
            <input type="text" onChange={this.handleDescription} value={this.state.description} name="description" id="description" /><br />
          <label> Color </label><br />
            <input type="radio" name="color" value="#61210f" id="color-61210f" onClick={this.handleColor} /><label className="color" htmlFor="color-61210f" style={{backgroundColor: "#61210f"}}></label>
            <input type="radio" name="color" value="#e9724c" id="color-e9724c" onClick={this.handleColor} /><label className="color" htmlFor="color-e9724c" style={{backgroundColor: "#e9724c"}}></label>
            <input type="radio" name="color" value="#edff1b" id="color-edff1b" onClick={this.handleColor} /><label className="color" htmlFor="color-edff1b" style={{backgroundColor: "#edff1b"}}></label>
            <input type="radio" name="color" value="#5dff4e" id="color-5dff4e" onClick={this.handleColor} /><label className="color" for="color-5dff4e"></label>
            <input type="radio" name="color" value="#5dff4e" id="color-5dff4e" onClick={this.handleColor} /><label className="color" for="color-5dff4e"></label>
            <input type="radio" name="color" value="#5a67fc" id="color-5a67fc" onClick={this.handleColor} /><label className="color" for="color-5a67fc"></label>
            <input type="radio" name="color" value="#b63ff3" id="color-b63ff3" onClick={this.handleColor} /><label className="color" for="color-b63ff3"></label>
            <input type="radio" name="color" value="#000000" id="color-000000" onClick={this.handleColor} /><label className="color" for="color-000000"></label>
            <input type="radio" name="color" value="#ffffff" id="color-ffffff" onClick={this.handleColor} /><label className="color" for="color-ffffff"></label>
            <br />
            <input type="submit" />
        </form>
      </div>
    )
  }
}

export default PinInput;
