import React from 'react';

const PinControls = (props) => {
  return (
    <React.Fragment>
      <div className="pin-controls">
        <button alt="add"><i className="material-icons" onClick={()=>props.togglePinInput(null)}>add_circle</i></button>
        <button className="hover-div" alt="filter">
          <i className="material-icons" onClick={()=>props.viewButton({type: 'SHOW_PINS_BY_COLOR', criteria: "#ffffff"})}>filter_list</i>
          <div className='hover-div--on'>
              <button alt="edit">
                 <i className="material-icons small" onClick={()=>props.togglePinInput(props.info.id)}>
                 edit</i>
               </button>
          </div>
        </button>
      </div>
    </React.Fragment>
  )
}

export default PinControls;
