import React from 'react';
import CanvasContainer from './containers/CanvasContainer.js';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import fetchPins from './actions/fetchPins.js'
import { connect } from 'react-redux';

const App = (props) => {
  return (
    <Router>
      {<div className="App">
        <CanvasContainer />
        <Switch>
          <Route path="/maps/:id" render={(props) => fetchPins(props) } />
        </Switch>
      </div>}
    </Router>
  );
}

const mapStateToProps = (state) => {
  return { pins: getVisiblePins(state.pinReducer, state.filterReducer) }
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadPins: (id) => dispatch({type: 'LOAD_PINS', id})
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
