export const fetchMapInfo = function (id) {
  return (dispatch) => {
    dispatch ({type: 'LOADING_MAP'})
    fetch(`/maps/${id}`,
      { accept: 'application/json',})
      .then(res => res.json())
      .then(json => {
        console.log(json)
        return {
          type: 'LOAD_PINS',
          json
        };
      })
  }
}
export const fetchPins = function(id){
    fetch(`/maps/${id}/pins`,
      { accept: 'application/json',})
      .then(res => res.json())
      .then(json => json)
  }

export default fetchPins;
