import React from 'react';

const Pin = (props) => {
  return(
      <div className="pin"
         style={{
           backgroundColor: props.info.color,
           top: `${props.info.location[1]}px`,
           left: `${props.info.location[0]}px`
         }}>
         <div className='hover-div'>
           <div className='hover-div--on'>
               {props.info.name}
           </div>
         </div>
      </div>
  )
}

export default Pin;
