 const filterReducer = (state = {filter: 'SHOW_ALL', id: null, color: null}, action) => {
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER':
      return action.filter
    default:
      return state
  }
}


export default filterReducer;
