import React from 'react';
import background from '../../worldpin_canvas_background.png';

class CreateCanvas extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      title: "My WorldPin",
      url: null
    }
  }

  handleSubmit = () => {

  }

  handleTitle = () => {

  }

  handleUrl = () => {

  }

  render() {
    return (
      <div id="create-canvas">
        <div class="canvas-map">
          <img id="canvas" src={background} alt="canvas" />
        </div>
        <header className="canvas-title">
          <h1>Create a New Canvas</h1>
        </header>
        <div className="canvas-input">
          <form onSubmit={event => this.handleSubmit(event, this.state)}>
            <label> <u>Title:</u> </label><br />
              Choose a title - preferrably 10 characters or less.<br />
              <input type="text" onChange={this.handleTitle} value={this.state.title} name="title" id="title" /><br />
            <label> <u>Map URL:</u> </label><br />
              Input a url (if desired). To use default map, leave blank.<br />
              <input type="text" onChange={this.handleUrl} value={this.state.url} name="url" id="url" /><br />
              <input type="submit" />
          </form>
        </div>
      </div>
    )
  }
}

export default CreateCanvas;
