import React from 'react';

const Pin = (props) => {
  return(
      <div className="pin hover-div"
         style={{
           backgroundColor: props.info.color,
           top: `${props.info.y}px`,
           left: `${props.info.x}px`
         }}>
         <div className='hover-div--on'>
             <button alt="edit">
                <a onClick={(e)=>props.togglePinInput(e, props.info)} href={`/maps/${props.canvasId}/pins/${props.info.id}/edit`}>
                  <i className="material-icons small">
                edit</i>
                </a>
              </button>
             <button alt="cancel">
                <i className="material-icons small" onClick={()=>props.delete(props.info.id)}>cancel</i>
              </button><br />
              <u>{props.info.name}</u><br />
             {props.info.description}<br />
         </div>
      </div>
  )
}

export default Pin;
