import React, { PureComponent } from 'react'
import PinAdd from '../components/pins/PinAdd.js';
import PinControls from '../components/pins/PinControls.js';
import PinList from '../components/pins/PinList.js';
import { connect } from 'react-redux';

// Handles all of the pin logic and display

class PinContainer extends PureComponent {

constructor(){
  super()
  this.state = {
    pinAdd: false,
    pinControls: false,
    pinAdd: false
  }
}

togglePinAdd = (event) => {
  let map = document.getElementById('root')
  this.setState({pinAdd: !this.state.pinAdd})
  !this.state.pinAdd ? map.style.cursor="crosshair" : map.style.cursor="default";
}

togglePinEdit = (event) => {
  let map = document.getElementById('root')
  this.setState({pinEdit: !this.state.pinEdit})
  !this.state.pinEdit ? map.style.cursor="crosshair" : map.style.cursor="default";
}

toggleControls = (event) => {
  event.preventDefault();
  this.setState({pinControls: !this.state.pinControls})
}

viewButton = () => {
  console.log("view")
}

handleSubmit = (event, data) => {
  event.preventDefault()
  data.location = this.props.capturedClick
  this.props.createPin(data)
  this.togglePinAdd()
}

  render() {
    return (
      <React.Fragment>
          {!!this.state.pinControls ? <PinControls addButton={this.togglePinAdd} editButton={this.togglePinEdit} viewButton={this.viewButton} /> : null }
          {!!this.state.pinAdd ? <PinAdd id={null} handleSubmit={this.handleSubmit} hide={this.togglePinAdd} /> : null}
          <PinList browserSize={this.props.browserSize} difference={this.props.difference} pins={this.props.pins} />
          <button id="pin-controls-toggle" onClick={this.toggleControls} alt="more"><i className="material-icons">settings</i></button>
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  return { pins: state }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createPin: (data) => {dispatch({type: 'CREATE_PIN', data})},
    deletePin: (data) => {dispatch({type: 'DELETE_PIN', data})},
    editPin: () => {dispatch()},
    filterPins: () => {dispatch()}
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(PinContainer);
