import React from 'react';
import CanvasContainer from './containers/CanvasContainer.js';
import './App.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { connect } from 'react-redux';

const App = (props) => {
  return (
    <Router>
      {<div className="App">
        <CanvasContainer />
        <Switch>
          <Route path="/maps/:id" render={(props) => fetchPins(props) } />
        </Switch>
      </div>}
    </Router>
  );
}

const fetchPins = function (props){
  let id = props.match.params.id
  fetch(`/maps/${id}`,
    { accept: 'application/json',})
    .then(res => res.json())
    .then(json => this.loadPins(json))
}

const mapDispatchToProps = (dispatch) => {
  return {
    loadPins: (id) => dispatch({type: 'LOAD_PINS', id})
  }
}

export default connect(mapDispatchToProps)(App);
