import React from 'react';
import PinPopup from './PinPopup.js';

const Pin = (props) => {
  return(
    <PinPopup onHover={
      <div>
          {props.name}
      </div>}>
      <div className="pin"
         style={{
           backgroundColor: props.info.color,
           top: `${props.info.location[1]}px`,
           left: `${props.info.location[0]}px`
         }}>
      </div>
   </PinPopup>
  )
}

export default Pin;
