export const fetchMapInfo = function(id){
  fetch(`/maps/${id}`,
    { accept: 'application/json',})
    .then(res => res.json())
    .then(json => {
      return {
        type: 'LOAD_PINS',
        json
      };
    })
  };

export const fetchPins = function(id){
    fetch(`/maps/${id}/pins`,
      { accept: 'application/json',})
      .then(res => res.json())
      .then(json => json)
  }

export default fetchPins;
