import { VisibilityFilters } from '../actions'

 const filterReducer = (state = 'SHOW_ALL', action = {type: 'SHOW_ALL', criteria: null}) => {
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER':
      return action
    default:
      return state
  }
}


export default filterReducer;
