export const fetchMapInfo = (id) => {
    console.log(id)
  return (dispatch, id) => {
    dispatch ({type: 'LOADING_MAP'})
    fetch(`/maps/${id}`)
      .then(res => res.json())
      .then(json => {
        return {
          type: 'LOAD_PINS', json
        };
      })
  }
}

