import React from 'react';

const Pin = (props) => {
  const handleClick = (id) => {
    props.toggleParentInput(id)
  }

  return(
      <div className="pin hover-div"
         style={{
           backgroundColor: props.info.color,
           top: `${props.info.location[1]}px`,
           left: `${props.info.location[0]}px`
         }}>
         <div className='hover-div--on'>
             <button alt="edit">
                <i className="material-icons small" onClick={()=>handleClick(props.info.id)}>
                edit</i>
              </button>
             <button alt="cancel">
                <i className="material-icons small" onClick={console.log("delete")}>cancel</i>
              </button><br />
              <u>{props.info.name}</u><br />
             {props.info.description}<br />
         </div>
      </div>
  )
}

export default Pin;
