const fetchMapInfo = (props) => {
  let id = props.match.params.id
  fetch(`/maps/${id}`,
    { accept: 'application/json',})
    .then(res => res.json())
    .then(json => json)
  };

const fetchPins = (props) => {
    let id = props.match.params.id
    fetch(`/maps/${id}/pins`,
      { accept: 'application/json',})
      .then(res => res.json())
      .then(json => json)
  }

export default {fetchPins, fetchMapInfo};
