 const filterReducer = (state = {filter: 'SHOW_ALL'}, action = {id: null, color: null}) => {
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER':
      return action.filter
    default:
      return state
  }
}


export default filterReducer;
