import React from 'react';
import PinPopup from './PinPopup.js';

const Pin = (props) => {
  return(
    <div className="pin"
      style={{
        backgroundColor: props.info.color,
        top: `${props.info.location[1]/(props.scale.x)}px`,
        left: `${props.info.location[0]/(props.scale.y)}px`
      }}>
      <a onClick={<PinPopup info={props.info} />} href="/"><label></label></a>
    </div>
  )
}

export default Pin;
