  fetchMapInfo: (id) => {
    debugger
  return (dispatch, id) => {
    dispatch ({type: 'LOADING_MAP'})
    fetch(`/maps/${id}`)
      .then(res => res.json())
      .then(json => dispatch({type: 'LOAD_PINS', json}))
  }
},

// for some reason, this isn't triggering the actino.

 fetchPins: (id) => {
    fetch(`/maps/${id}/pins`,
      { accept: 'application/json',})
      .then(res => res.json())
      .then(json => json)
  }
}

